mapper.select:
select z1.ZD_ID, z1.NAME, z1.BIANMA, z1.ORDY_BY, z1.PARENT_ID, z1.JB, z1.P_BM, z2.NAME PNAME from SYS_DICTIONARIES z1 left join SYS_DICTIONARIES z2 on z1.PARENT_ID = z2.ZD_ID where z1.PARENT_ID = #{pd.PARENT_ID}
and z1.NAME LIKE CONCAT(CONCAT('%', #{pd.NAME}),'%')
order by z1.ORDY_BY
